BB : matrix([rand(8) - 4], [rand(8) - 4], [rand(8) - 4]);
eqq : AA . vecX;
eqq1 : eqq[1][1] = BB[1][1];
eqq2 : eqq[2][1] = BB[2][1];
eqq3 : eqq[3][1] = BB[3][1];

detAA : determinant(AA);

